// Function expression


const repeatString = function(aString, stringMultiplier) {
    let appendedString = ""
    for(let i = 0; i < stringMultiplier; i++){
        appendedString += aString
    }
    return appendedString











};

/* 
- Use a loop 
- repeats the given number of times and add given string to the result on each loop
- create a variable to hold string that you're going to return



*/
// Do not edit below this line
module.exports = repeatString;
